#include&amp;lt;cstring&amp;gt;
#include&amp;lt;cstdio&amp;gt;

using namespace std;

int f(int n) &amp;#123;
    if(n==1) return 1;
    if(n==2) return 2;
    return f(n-1) + f(n-2);
&amp;#125;

int main() &amp;#123;
    int n;
