      this.pageSize = pageSize
      //用更新后的数据再请求
      this.load()
    &amp;#125;,
    handleCurrentChange(pageNum) &amp;#123;
      console.log(pageNum);
      this.pageNum = pageNum
      //用更新后的数据再请求
      this.load()
    &amp;#125;,
    handleFileUploadSuccess(res)&amp;#123;
      this.$message.success(&amp;quot;上传成功&amp;quot;)
      this.load()
      console.log(res)
    &amp;#125;,
    download(url)&amp;#123;
      window.open(url)
    &amp;#125;,
    changeEnable(row)&amp;#123;
      this.request.post(&amp;quot;/file/update&amp;quot;,row).then(res =&amp;gt;&amp;#123;
        if (res.code === &#39;200&#39;)&amp;#123;
          this.$message.success(&amp;quot;操作成功&amp;quot;)
        &amp;#125;
      &amp;#125;)
    &amp;#125;,

  &amp;#125;
&amp;#125;
&amp;lt;/script&amp;gt;

